contracts/oracles/GlobalRepoRateOracle.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IPriceOracle} from "../interfaces/IPriceOracle.sol";

/**
 * @title GlobalRepoRateOracle
 * @notice An oracle that provides a "global repo rate" for fiat-pegged stablecoins.
 * This is a simplified model where the owner can set a rate that influences
 * the interest rate models for stablecoins.
 */
contract GlobalRepoRateOracle is Ownable, IPriceOracle {
    mapping(address => uint256) private _prices;
    uint256 private globalRepoRate; // Scaled by 1e18
    uint8 private constant PRICE_DECIMALS = 8;

    event PriceUpdated(address indexed asset, uint256 price);
    event GlobalRepoRateUpdated(uint256 newRate);

    constructor(address admin) Ownable(admin) {
        // Initialize with a default rate, e.g., 2.5%
        globalRepoRate = 25 * 1e15; // 0.025 * 1e18
    }

    /**
     * @notice Sets the global repo rate.
     * @param newRate The new rate, scaled by 1e18.
     */
    function setGlobalRepoRate(uint256 newRate) external onlyOwner {
        globalRepoRate = newRate;
        emit GlobalRepoRateUpdated(newRate);
    }

    /**
     * @notice Gets the current global repo rate.
     */
    function getGlobalRepoRate() external view returns (uint256) {
        return globalRepoRate;
    }

    /**
     * @notice Sets the price for a given asset (for stablecoins, this should be ~1 USD).
     * @param asset The address of the asset.
     * @param price The price to set, scaled by 1e8.
     */
    function setPrice(address asset, uint256 price) external onlyOwner {
        _prices[asset] = price;
        emit PriceUpdated(asset, price);
    }

    /**
     * @notice Returns the price of a specific asset. For stablecoins, this will be
     * close to 1 USD, but this oracle doesn't enforce it.
     * @param asset The address of the asset's ERC20 contract.
     * @return The price of the asset, scaled by 1e8.
     */
    function getPrice(address asset) external view override returns (uint256) {
        uint256 price = _prices[asset];
        if (price == 0) {
            revert("Price not set for this asset");
        }
        return price;
    }

    /**
     * @notice Returns the number of decimals used for the price.
     */
    function decimals() external pure override returns (uint8) {
        return PRICE_DECIMALS;
    }
}

contracts/test/MaliciousERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./ReentrancyAttacker.sol";

interface ILendingPool {
    function withdraw(address _asset, uint256 _shares) external;
}

contract MaliciousERC20 is ERC20, Ownable {
    ILendingPool private lendingPool;
    address private attackerContractAddress;

    constructor(address _lendingPool) ERC20("Malicious Token", "MTKN") Ownable(msg.sender) {
        lendingPool = ILendingPool(_lendingPool);
    }

    function setAttacker(address _attacker) public onlyOwner {
        attackerContractAddress = _attacker;
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function _update(address from, address to, uint256 value) internal override {
        if (to == attackerContractAddress) {
            // This is the re-entrant part of the attack.
            // When the lending pool sends tokens to the attacker, this hook is called.
            // The attacker then calls withdraw again before the first withdraw is complete.
            ReentrancyAttacker(payable(attackerContractAddress)).reenter();
        }
        super._update(from, to, value);
    }
}

