// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockUSD
 * @notice A mock USD-pegged stablecoin for testing purposes.
 */
contract MockUSD is ERC20, Ownable {
    constructor(address admin) ERC20("Mock USD", "mUSD") Ownable(admin) {}

    /**
     * @notice Mints new tokens to a specified address.
     * @param to The address to mint tokens to.
     * @param amount The amount of tokens to mint.
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }
}

contracts/test/ReentrancyAttacker.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../LendingPool.sol";

contract ReentrancyAttacker {
    LendingPool private immutable pool;
    address private immutable asset;

    constructor(address _pool, address _asset) {
        pool = LendingPool(_pool);
        asset = _asset;
    }

    function attack(uint256 amount) external {
        pool.withdraw(asset, amount);
    }

    function reenter() external {
        pool.withdraw(asset, 1);
    }

    // Fallback function to receive Ether
    receive() external payable {}
    fallback() external payable {}
}

