contracts/bridge/ChainRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ChainRegistry
 * @notice Registry of supported chains, gateway addresses and token allowlists.
 *         Acts as a single source of truth to (a) exclude meme/unsupported chains,
 *         (b) whitelist tokens per destination, and (c) classify chain categories.
 */
contract ChainRegistry is Ownable {
    constructor() Ownable(msg.sender) {}
    enum ChainKind { EVM, UTXO, COSMOS, SUBSTRATE, SOLANA, CARDANO, OTHER }

    struct ChainInfo {
        bool enabled;
        ChainKind kind;
        address gateway; // gateway contract/program address on that chain (EVM address or adapter id)
    }

    // chainId (per chain's native id for EVM; otherwise a project-defined id) => info
    mapping(uint256 => ChainInfo) public chains;

    // token allowlist per chainId => token address => allowed
    mapping(uint256 => mapping(address => bool)) public allowedTokens;

    event ChainSet(uint256 indexed chainId, ChainKind kind, address gateway, bool enabled);
    event TokenAllowed(uint256 indexed chainId, address indexed token, bool allowed);

    function setChain(
        uint256 chainId,
        ChainKind kind,
        address gateway,
        bool enabled
    ) external onlyOwner {
        chains[chainId] = ChainInfo({enabled: enabled, kind: kind, gateway: gateway});
        emit ChainSet(chainId, kind, gateway, enabled);
    }

    function setTokenAllowed(
        uint256 chainId,
        address token,
        bool allowed
    ) external onlyOwner {
        allowedTokens[chainId][token] = allowed;
        emit TokenAllowed(chainId, token, allowed);
    }

    function isChainEnabled(uint256 chainId) external view returns (bool) {
        return chains[chainId].enabled;
    }

    function isTokenAllowed(uint256 chainId, address token) external view returns (bool) {
        return allowedTokens[chainId][token];
    }

    function gatewayFor(uint256 chainId) external view returns (address) {
        return chains[chainId].gateway;
    }

    function kindOf(uint256 chainId) external view returns (ChainKind) {
        return chains[chainId].kind;
    }
}

contracts/bridge/GatewayV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./WrappedToken.sol";
import "./RelayerManager.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title GatewayV2
 * @dev This gateway requires a threshold of signatures from a decentralized set of
 * relayers to authorize the minting of wrapped tokens.
 */
contract GatewayV2 is ReentrancyGuard {
    using ECDSA for bytes32;

    RelayerManager public immutable relayerManager;
    address public immutable wrappedToken;
    mapping(bytes32 => bool) public usedProofs; // Prevents replay attacks

    event TokensMinted(
        address indexed recipient,
        uint256 amount,
        bytes32 indexed sourceTxHash
    );

    event MessageExecuted(
        address indexed target,
        bytes32 indexed sourceTxHash,
        bool success,
        bytes returnData
    );

    constructor(address _relayerManager, address _wrappedToken) {
        require(_relayerManager != address(0), "Invalid RelayerManager address");
        require(_wrappedToken != address(0), "Invalid WrappedToken address");
        relayerManager = RelayerManager(_relayerManager);
        wrappedToken = _wrappedToken;
    }

    /**
     * @dev Mints new wrapped tokens to a recipient after verifying a quorum of relayer signatures.
     * @param recipient The address that will receive the minted tokens.
     * @param amount The amount of tokens to mint.
     * @param sourceTxHash A unique hash from the source chain transaction to prevent replays.
     * @param signatures An array of signatures from the relayers.
     */
    function mint(
        address recipient,
        uint256 amount,
        bytes32 sourceTxHash,
        bytes[] calldata signatures
    ) external {
        require(!usedProofs[sourceTxHash], "Proof has already been used");

        uint256 relayerCount = relayerManager.getRelayerCount();
        uint256 requiredSignatures = (relayerCount * 2) / 3 + 1;

        require(signatures.length >= requiredSignatures, "Insufficient signatures");

    bytes32 proofHash = keccak256(abi.encodePacked(recipient, amount, sourceTxHash, block.chainid));
    // Construct Ethereum Signed Message hash ("\x19Ethereum Signed Message:\n32" + proofHash)
    bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", proofHash));

        address[] memory signers = new address[](signatures.length);

        for (uint i = 0; i < signatures.length; i++) {
            address signer = ECDSA.recover(messageHash, signatures[i]);
            
            // Ensure the signer is an active relayer
            require(relayerManager.isRelayer(signer), "Signature from non-relayer");

            // Ensure signatures are unique
            for (uint j = 0; j < i; j++) {
                require(signers[j] != signer, "Duplicate signature");
            }
            signers[i] = signer;
        }

        usedProofs[sourceTxHash] = true;

        // Mint through the configured WrappedToken contract. The WrappedToken should
        // have its owner set to this GatewayV2 instance so only this contract can mint.
        WrappedToken(wrappedToken).mint(recipient, amount);

        emit TokensMinted(recipient, amount, sourceTxHash);
    }

    /**
     * @dev Execute an arbitrary message on `target` after verifying a quorum of relayer signatures.
     * The proof is computed as keccak256(abi.encodePacked(target, payload, sourceTxHash, chainid)).
     * This function is protected by a reentrancy guard and checks payload size.
     */
    function executeMessage(
        address target,
        bytes calldata payload,
        bytes32 sourceTxHash,
        bytes[] calldata signatures
    ) external nonReentrant {
        require(target != address(0), "Invalid target");
        require(payload.length <= 4096, "Payload too large");
        require(!usedProofs[sourceTxHash], "Proof has already been used");

        uint256 relayerCount = relayerManager.getRelayerCount();
        uint256 requiredSignatures = (relayerCount * 2) / 3 + 1;
        require(signatures.length >= requiredSignatures, "Insufficient signatures");

        bytes32 proofHash = keccak256(abi.encodePacked(target, payload, sourceTxHash, block.chainid));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", proofHash));

        address[] memory signers = new address[](signatures.length);
        for (uint i = 0; i < signatures.length; i++) {
            address signer = ECDSA.recover(messageHash, signatures[i]);
            require(relayerManager.isRelayer(signer), "Signature from non-relayer");
            for (uint j = 0; j < i; j++) {
                require(signers[j] != signer, "Duplicate signature");
            }
            signers[i] = signer;
        }

        usedProofs[sourceTxHash] = true;

        // Execute the payload on the target contract and emit result.
        (bool success, bytes memory ret) = target.call(payload);
        emit MessageExecuted(target, sourceTxHash, success, ret);
    }
}

contracts/bridge/GatewayV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./RelayerManager.sol";
import "./WrappedToken.sol";
import "./ChainRegistry.sol";
import "./MessageLib.sol";

/**
 * @title GatewayV3
 * @notice Hybrid cross-chain gateway:
 *  - Token bridging (lock/mint, burn/release) with quorum signatures
 *  - Generalized message passing (cross-chain call) with quorum signatures
 *  - Chain + token allowlisting via ChainRegistry
 *  - Circuit breaker via Pausable
 */
contract GatewayV3 is Ownable, Pausable {
    RelayerManager public immutable relayerManager;
    ChainRegistry public immutable registry;

    // Tracks which source proofs/messages have already been consumed.
    mapping(bytes32 => bool) public usedProofs;

    // Simple incremental nonce for local events.
    uint256 public nonce;

    event TokensLocked(
        address indexed sender,
        address indexed token,
        uint256 amount,
        uint256 toChainId,
        address recipient,
        uint256 nonce
    );

    event TokensMinted(
        address indexed recipient,
        address indexed wrappedToken,
        uint256 amount,
        bytes32 sourceTxHash
    );

    event MessageSent(
        address indexed sender,
        uint256 toChainId,
        address indexed target,
        bytes data,
        uint256 value,
        uint256 nonce
    );

    event MessageExecuted(
        address indexed executor,
        address indexed target,
        uint256 fromChainId,
        bytes data,
        bool success
    );

    constructor(RelayerManager _relayerManager, ChainRegistry _registry) Ownable(msg.sender) {
        relayerManager = _relayerManager;
        registry = _registry;
    }

    // -------------------- SENDER SIDE --------------------

    /**
     * @notice Lock ERC-20 tokens on this chain for bridging.
     * @dev Only tokens/chainIds allowlisted in ChainRegistry are accepted.
     */
    function bridgeToken(
        address token,
        uint256 amount,
        uint256 toChainId,
        address recipient
    ) external whenNotPaused {
        require(registry.isChainEnabled(toChainId), "dest chain disabled");
        require(registry.isTokenAllowed(toChainId, token), "token not allowed");
        require(amount > 0, "amount = 0");

        bool ok = IERC20(token).transferFrom(msg.sender, address(this), amount);
        require(ok, "transfer failed");

        uint256 n = ++nonce;
        emit TokensLocked(msg.sender, token, amount, toChainId, recipient, n);
    }

    /**
     * @notice Emit a generic cross-chain message to be executed on another chain.
     */
    function sendMessage(
        uint256 toChainId,
        address target,
        bytes calldata data
    ) external payable whenNotPaused {
        require(registry.isChainEnabled(toChainId), "dest chain disabled");
        uint256 n = ++nonce;
        emit MessageSent(msg.sender, toChainId, target, data, msg.value, n);
    }

    // -------------------- RECEIVER SIDE --------------------

    /**
     * @notice Mint wrapped tokens on this chain corresponding to a lock on a source chain,
     *         once enough relayer signatures over the sourceTxHash have been collected.
     */
    function mintWrapped(
        address wrappedToken,
        address recipient,
        uint256 amount,
        bytes32 sourceTxHash,
        bytes[] calldata signatures
    ) external whenNotPaused {
        require(!usedProofs[sourceTxHash], "proof used");

        _verifyQuorum(sourceTxHash, signatures);
        usedProofs[sourceTxHash] = true;

        WrappedToken(wrappedToken).mint(recipient, amount);

        emit TokensMinted(recipient, wrappedToken, amount, sourceTxHash);
    }

    /**
     * @notice Execute an arbitrary message that originated from another chain,
     *         once enough relayer signatures are available over the message hash.
     *
     * @dev For now we fix the cross-chain nonce to 0 inside the hash and rely
     *      on sourceMsgHash + usedProofs for replay protection.
     */
    function executeMessage(
        uint256 fromChainId,
        address sender,
        address target,
        bytes calldata data,
        uint256 value,
        bytes32 sourceMsgHash,
        bytes[] calldata signatures
    ) external whenNotPaused {
        require(!usedProofs[sourceMsgHash], "proof used");

        bytes32 expected = MessageLib.hashMessage(
            0, // cross-chain nonce placeholder
            fromChainId,
            block.chainid,
            sender,
            target,
            data,
            value
        );
        require(expected == sourceMsgHash, "hash mismatch");

        _verifyQuorum(sourceMsgHash, signatures);
        usedProofs[sourceMsgHash] = true;

        (bool ok, ) = target.call{value: value}(data);

        emit MessageExecuted(msg.sender, target, fromChainId, data, ok);
    }

    // -------------------- ADMIN --------------------

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // -------------------- INTERNAL --------------------

    /**
     * @dev Validate that a sufficient number of unique, active relayers have signed `digest`.
     */
    function _verifyQuorum(
        bytes32 digest,
        bytes[] calldata sigs
    ) internal view {
        uint256 threshold = relayerManager.signatureThreshold();
        require(threshold > 0, "threshold=0");
        require(sigs.length >= threshold, "not enough signatures");

        address[] memory seen = new address[](sigs.length);

        for (uint256 i = 0; i < threshold; i++) {
            address signer = _recover(digest, sigs[i]);
            require(relayerManager.isRelayer(signer), "invalid signer");

            // O(n^2) uniqueness check is fine for small thresholds.
            for (uint256 j = 0; j < i; j++) {
                require(seen[j] != signer, "duplicate signature");
            }
            seen[i] = signer;
        }
    }

    /**
     * @dev Recover signer from an arbitrary digest using the standard
     *      `"\x19Ethereum Signed Message:\n32"` prefix.
     */
    function _recover(
        bytes32 digest,
        bytes memory signature
    ) internal pure returns (address) {
        bytes32 ethHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", digest)
        );
        (bytes32 r, bytes32 s, uint8 v) = _split(signature);
        return ecrecover(ethHash, v, r, s);
    }

    function _split(
        bytes memory sig
    ) internal pure returns (bytes32 r, bytes32 s, uint8 v) {
        require(sig.length == 65, "bad sig length");
        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
        if (v < 27) {
            v += 27;
        }
    }
}

