contracts/governance/RateGovernor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";

/// @title RateGovernor
/// @notice Minimal timelocked governance helper for scheduling interest-rate model updates.
/// @dev The governor queues arbitrary calls to interest rate models (or any ownable contract)
///      and executes them after a configurable delay. Designed to own the interest rate
///      strategies so research-driven parameter adjustments can be rolled out safely.
contract RateGovernor is Ownable {

    struct Proposal {
        address target;
        bytes data;
        uint256 executeAfter;
        bool executed;
        bool cancelled;
    }

    uint256 public minDelay; // seconds
    Proposal[] private _proposals;

    event MinDelayUpdated(uint256 oldDelay, uint256 newDelay);
    event UpdateQueued(uint256 indexed proposalId, address indexed target, uint256 executeAfter, bytes data);
    event UpdateExecuted(uint256 indexed proposalId);
    event UpdateCancelled(uint256 indexed proposalId);

    error InvalidTarget();
    error NotReady();
    error AlreadyHandled();

    constructor(uint256 _minDelay, address admin) Ownable(admin) {
        minDelay = _minDelay;
    }

    function proposalCount() external view returns (uint256) {
        return _proposals.length;
    }

    function getProposal(uint256 proposalId) external view returns (Proposal memory) {
        return _proposals[proposalId];
    }

    function setMinDelay(uint256 newDelay) external onlyOwner {
        emit MinDelayUpdated(minDelay, newDelay);
        minDelay = newDelay;
    }

    function queueUpdate(address target, bytes calldata data, uint256 executeAfter)
        external
        onlyOwner
        returns (uint256 proposalId)
    {
        if (target == address(0)) revert InvalidTarget();

        uint256 earliest = block.timestamp + minDelay;
        if (executeAfter < earliest) {
            executeAfter = earliest;
        }

        proposalId = _proposals.length;
        _proposals.push(Proposal({
            target: target,
            data: data,
            executeAfter: executeAfter,
            executed: false,
            cancelled: false
        }));

        emit UpdateQueued(proposalId, target, executeAfter, data);
    }

    function cancel(uint256 proposalId) external onlyOwner {
        Proposal storage proposal = _proposals[proposalId];
        if (proposal.executed || proposal.cancelled) revert AlreadyHandled();
        proposal.cancelled = true;
        emit UpdateCancelled(proposalId);
    }

    function execute(uint256 proposalId) external {
        Proposal storage proposal = _proposals[proposalId];
        if (proposal.executed || proposal.cancelled) revert AlreadyHandled();
        if (block.timestamp < proposal.executeAfter) revert NotReady();

    proposal.executed = true;
    Address.functionCall(proposal.target, proposal.data);

        emit UpdateExecuted(proposalId);
    }
}

contracts/interest/ExponentialInterestRa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IInterestRateModel} from "../interfaces/IInterestRateModel.sol";

/// @notice Exponential model: r(u) = base + coeff * (exp(exponentFactor * u) - 1).
contract ExponentialInterestRateModel is Ownable, IInterestRateModel {
    uint256 public constant SECONDS_PER_YEAR = 365 days;

    uint256 public baseAPR;         // 1e18
    uint256 public coefficientAPR;  // 1e18 multiplier applied to exp growth
    uint256 public exponentFactor;  // 1e18 (controls curvature)

    event ParamsUpdated(uint256 baseAPR, uint256 coefficientAPR, uint256 exponentFactor);

    constructor(uint256 _baseAPR, uint256 _coefficientAPR, uint256 _exponentFactor, address admin)
        Ownable(admin)
    {
        _setParams(_baseAPR, _coefficientAPR, _exponentFactor);
    }

    function getBorrowRatePerSecond(uint256 utilization) external view override returns (uint256) {
        if (utilization > 1e18) utilization = 1e18;

        // exponentFactor * utilization remains within safe range via parameter bounds
        uint256 exponent = (exponentFactor * utilization) / 1e18;
        uint256 expValue = ExpMath.exp1e18(exponent);
        uint256 apr = baseAPR + (coefficientAPR * (expValue - 1e18)) / 1e18;
        return apr / SECONDS_PER_YEAR;
    }

    function updateBorrowRate(uint256) external pure override {}

    function setParams(uint256 _baseAPR, uint256 _coefficientAPR, uint256 _exponentFactor) external onlyOwner {
        _setParams(_baseAPR, _coefficientAPR, _exponentFactor);
    }

    function _setParams(uint256 _baseAPR, uint256 _coefficientAPR, uint256 _exponentFactor) internal {
        require(_baseAPR <= 1e18, "base too high");
        require(_coefficientAPR <= 10e18, "coeff too high");
        require(_exponentFactor <= 2e18, "exp too high");

        baseAPR = _baseAPR;
        coefficientAPR = _coefficientAPR;
        exponentFactor = _exponentFactor;

        emit ParamsUpdated(_baseAPR, _coefficientAPR, _exponentFactor);
    }
}

library ExpMath {
    uint256 private constant WAD = 1e18;

    /// @notice Approximate e^(x) where x and the result use 1e18 fixed-point decimals.
    /// Uses a 5-term Taylor series which is accurate for x in [0, 2e18].
    function exp1e18(uint256 x) internal pure returns (uint256) {
        require(x <= 2e18, "exp overflow");

        uint256 sum = WAD;
        uint256 term = WAD;

        term = (term * x) / WAD;
        sum += term;

        term = (term * x) / WAD;
        sum += term / 2;

        term = (term * x) / WAD;
        sum += term / 6;

        term = (term * x) / WAD;
        sum += term / 24;

        term = (term * x) / WAD;
        sum += term / 120;

        return sum;
    }
}

contracts/interest/KinkInterestRateModel.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IInterestRateModel} from "../interfaces/IInterestRateModel.sol";

/// @notice Piecewise linear "jump rate" model similar to Compound's IRM.
contract KinkInterestRateModel is Ownable, IInterestRateModel {
    uint256 public constant SECONDS_PER_YEAR = 365 days;

    uint256 public baseAPR;    // 1e18
    uint256 public slopeLowAPR; // 1e18
    uint256 public slopeHighAPR; // 1e18
    uint256 public kink; // utilization in 1e18

    event ParamsUpdated(uint256 baseAPR, uint256 slopeLowAPR, uint256 slopeHighAPR, uint256 kink);

    constructor(
        uint256 _baseAPR,
        uint256 _slopeLowAPR,
        uint256 _slopeHighAPR,
        uint256 _kink,
        address admin
    ) Ownable(admin) {
        _setParams(_baseAPR, _slopeLowAPR, _slopeHighAPR, _kink);
    }

    function getBorrowRatePerSecond(uint256 utilization) external view override returns (uint256) {
        if (utilization > 1e18) utilization = 1e18;

        uint256 apr;
        if (utilization <= kink) {
            apr = baseAPR + (slopeLowAPR * utilization) / 1e18;
        } else {
            uint256 head = baseAPR + (slopeLowAPR * kink) / 1e18;
            uint256 tail = (slopeHighAPR * (utilization - kink)) / 1e18;
            apr = head + tail;
        }
        return apr / SECONDS_PER_YEAR;
    }

    function updateBorrowRate(uint256) external pure override {}

    function setParams(
        uint256 _baseAPR,
        uint256 _slopeLowAPR,
        uint256 _slopeHighAPR,
        uint256 _kink
    ) external onlyOwner {
        _setParams(_baseAPR, _slopeLowAPR, _slopeHighAPR, _kink);
    }

    function _setParams(
        uint256 _baseAPR,
        uint256 _slopeLowAPR,
        uint256 _slopeHighAPR,
        uint256 _kink
    ) internal {
        require(_kink <= 1e18, "kink too high");
        require(_baseAPR <= 1e18, "base too high");
        require(_slopeLowAPR <= 5e18, "slope1 too high");
        require(_slopeHighAPR <= 10e18, "slope2 too high");

        baseAPR = _baseAPR;
        slopeLowAPR = _slopeLowAPR;
        slopeHighAPR = _slopeHighAPR;
        kink = _kink;

        emit ParamsUpdated(_baseAPR, _slopeLowAPR, _slopeHighAPR, _kink);
    }
}

contracts/interest/LinearInterestRateMod.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IInterestRateModel} from "../interfaces/IInterestRateModel.sol";

/// @notice Linear rate model: r(u) = base + slope * u
contract LinearInterestRateModel is Ownable, IInterestRateModel {
    uint256 public constant SECONDS_PER_YEAR = 365 days;

    uint256 public baseAPR;   // 1e18 scale
    uint256 public slopeAPR;  // 1e18 scale

    event APRUpdated(uint256 oldBaseAPR, uint256 oldSlopeAPR, uint256 newBaseAPR, uint256 newSlopeAPR);

    constructor(uint256 _baseAPR, uint256 _slopeAPR, address admin) Ownable(admin) {
        _setAPR(_baseAPR, _slopeAPR);
    }

    function getBorrowRatePerSecond(uint256 utilization) external view override returns (uint256) {
        if (utilization > 1e18) utilization = 1e18;
        uint256 apr = baseAPR + (slopeAPR * utilization) / 1e18;
        return apr / SECONDS_PER_YEAR;
    }

    function updateBorrowRate(uint256) external pure override {}

    function setAPR(uint256 _baseAPR, uint256 _slopeAPR) external onlyOwner {
        _setAPR(_baseAPR, _slopeAPR);
    }

    function _setAPR(uint256 _baseAPR, uint256 _slopeAPR) internal {
        require(_baseAPR <= 1e18, "base too high");
        require(_slopeAPR <= 5e18, "slope too high");
        emit APRUpdated(baseAPR, slopeAPR, _baseAPR, _slopeAPR);
        baseAPR = _baseAPR;
        slopeAPR = _slopeAPR;
    }
}

contracts/interest/TimeWeightedInterestR.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IInterestRateModel} from "../interfaces/IInterestRateModel.sol";

/// @notice Adaptive utilization-sensitive rate model inspired by Fraxlend's time-weighted design.
/// @dev The model keeps a mutable APR state that nudges upward when utilization stays above the
///      target band, downward when below, and reverts toward a neutral APR when healthy.
contract TimeWeightedInterestRateModel is Ownable, IInterestRateModel {
    uint256 public constant SECONDS_PER_YEAR = 365 days;
    uint256 private constant MAX_ADJUSTMENT_RATE = 5e15; // 0.5% APR change per second at full overshoot

    address public pool; // lending pool allowed to drive updates

    uint256 public minAPR;       // 1e18 annualized APR lower bound
    uint256 public maxAPR;       // 1e18 annualized APR upper bound
    uint256 public neutralAPR;   // 1e18 APR that the system reverts to inside the target band
    uint256 public adjustmentRatePerSecond; // 1e18 APR delta per second at 100% overshoot
    uint256 public lowerUtilization; // 1e18 utilization floor of healthy band
    uint256 public upperUtilization; // 1e18 utilization ceiling of healthy band

    uint256 public currentAPR;   // 1e18 APR that accrues interest
    uint256 public lastUpdate;   // timestamp of latest adjustment

    event PoolConfigured(address indexed pool);
    event ParametersUpdated(
        uint256 minAPR,
        uint256 maxAPR,
        uint256 neutralAPR,
        uint256 adjustmentRatePerSecond,
        uint256 lowerUtilization,
        uint256 upperUtilization
    );
    event RateAdjusted(uint256 utilization, uint256 newAPR, uint256 timestamp);

    error PoolAlreadySet();
    error PoolNotConfigured();
    error UnauthorizedUpdater();
    error InvalidBounds();
    error InvalidAdjustmentRate();

    constructor(
        uint256 _minAPR,
        uint256 _maxAPR,
        uint256 _neutralAPR,
        uint256 _adjustmentRatePerSecond,
        uint256 _lowerUtilization,
        uint256 _upperUtilization,
        address admin
    ) Ownable(admin) {
        _setParameters(
            _minAPR,
            _maxAPR,
            _neutralAPR,
            _adjustmentRatePerSecond,
            _lowerUtilization,
            _upperUtilization
        );
        currentAPR = neutralAPR;
        lastUpdate = block.timestamp;
    }

    /// @notice One-time hook invoked post-deployment to anchor the pool address.
    function setPool(address _pool) external {
        if (pool != address(0)) revert PoolAlreadySet();
        require(_pool != address(0), "pool zero");
        pool = _pool;
        lastUpdate = block.timestamp;
        emit PoolConfigured(_pool);
    }

    function getBorrowRatePerSecond(uint256) external view override returns (uint256) {
        if (currentAPR == 0) return 0;
        return currentAPR / SECONDS_PER_YEAR;
    }

    function updateBorrowRate(uint256) external override {
        if (pool == address(0)) revert PoolNotConfigured();
        if (msg.sender != pool) revert UnauthorizedUpdater();

        uint256 util = ILendingPoolLike(pool).utilization();
        _adjust(util);
    }

    function setParameters(
        uint256 _minAPR,
        uint256 _maxAPR,
        uint256 _neutralAPR,
        uint256 _adjustmentRatePerSecond,
        uint256 _lowerUtilization,
        uint256 _upperUtilization
    ) external onlyOwner {
        _setParameters(
            _minAPR,
            _maxAPR,
            _neutralAPR,
            _adjustmentRatePerSecond,
            _lowerUtilization,
            _upperUtilization
        );

        // Clamp the live APR into the new bounds immediately
        if (currentAPR < minAPR) currentAPR = minAPR;
        if (currentAPR > maxAPR) currentAPR = maxAPR;
    }

    function _adjust(uint256 utilization) internal {
        uint256 previousUpdate = lastUpdate;
        uint256 elapsed = block.timestamp - previousUpdate;
        if (elapsed == 0) return;

        uint256 apr = currentAPR;

        if (utilization > upperUtilization) {
            uint256 overshoot = utilization - upperUtilization;
            uint256 step = (adjustmentRatePerSecond * overshoot) / 1e18;
            uint256 delta = step * elapsed;
            apr += delta;
        } else if (utilization < lowerUtilization) {
            uint256 deficit = lowerUtilization - utilization;
            uint256 step = (adjustmentRatePerSecond * deficit) / 1e18;
            uint256 delta = step * elapsed;
            if (delta >= apr || apr - delta < minAPR) {
                apr = minAPR;
            } else {
                apr -= delta;
            }
        } else {
            if (apr > neutralAPR) {
                uint256 diff = apr - neutralAPR;
                uint256 delta = (adjustmentRatePerSecond * elapsed) / 4;
                if (delta > diff) delta = diff;
                apr -= delta;
            } else if (apr < neutralAPR) {
                uint256 diff = neutralAPR - apr;
                uint256 delta = (adjustmentRatePerSecond * elapsed) / 4;
                if (delta > diff) delta = diff;
                apr += delta;
            }
        }

        if (apr > maxAPR) apr = maxAPR;
        if (apr < minAPR) apr = minAPR;

        currentAPR = apr;
        lastUpdate = block.timestamp;
        emit RateAdjusted(utilization, apr, block.timestamp);
    }

    function _setParameters(
        uint256 _minAPR,
        uint256 _maxAPR,
        uint256 _neutralAPR,
        uint256 _adjustmentRatePerSecond,
        uint256 _lowerUtilization,
        uint256 _upperUtilization
    ) internal {
        if (_minAPR > _maxAPR) revert InvalidBounds();
        if (_neutralAPR < _minAPR || _neutralAPR > _maxAPR) revert InvalidBounds();
        if (_upperUtilization > 1e18 || _lowerUtilization > _upperUtilization) revert InvalidBounds();
        if (_adjustmentRatePerSecond == 0 || _adjustmentRatePerSecond > MAX_ADJUSTMENT_RATE) {
            revert InvalidAdjustmentRate();
        }

        minAPR = _minAPR;
        maxAPR = _maxAPR;
        neutralAPR = _neutralAPR;
        adjustmentRatePerSecond = _adjustmentRatePerSecond;
        lowerUtilization = _lowerUtilization;
        upperUtilization = _upperUtilization;

        emit ParametersUpdated(
            _minAPR,
            _maxAPR,
            _neutralAPR,
            _adjustmentRatePerSecond,
            _lowerUtilization,
            _upperUtilization
        );
    }
}

interface ILendingPoolLike {
    function utilization() external view returns (uint256);
}

